con:restMockService.con:onRequestScript:
// Overloaded dispatcher by Gutowski (SoapUI mcoks do not support URL params)

def requestPath = mockRequest.getPath()
def serviceMetadataMatcher = requestPath =~ /\/(?<serviceGroupId>[^\/]+)\/services\/(?<documentTypeId>[^\/]+)/
def serviceGroupMatcher    = requestPath =~ /\/(?<serviceGroupId>[^\/]+)/
def action = "BAD_REQUEST"
def httpMethod = mockRequest.getMethod().name()
def methodIsSupported = ["GET", "PUT", "DELETE"].contains(httpMethod)

log.info "Recieved the request: " + httpMethod + " " + requestPath
log.info "This HTTP method is supported: " + methodIsSupported

if(methodIsSupported){
	 if(serviceGroupMatcher.matches()){
		context.serviceGroupId = serviceGroupMatcher.group( 'serviceGroupId' )
		action = httpMethod + " ServiceGroup"
		log.info action + ", serviceGroupId=[" + context.serviceGroupId + "]"
	} else if(serviceMetadataMatcher.matches()){
		context.serviceGroupId = serviceMetadataMatcher.group( 'serviceGroupId' )
		context.documentTypeId = serviceMetadataMatcher.group( 'documentTypeId' )
		action = httpMethod + " ServiceMetadata"
		log.info action + ", serviceGroupId=[" + context.serviceGroupId + "], documentTypeId=[" + context.documentTypeId + "]"
	}
}

log.info "Returning response: " + action
mockRunner.mockContext.mockService.getMockOperationByName(action).dispatchRequest(mockRequest)

