con:restMockService.con:onRequestScript:
/*
 * SoapUI mocking framework does not support URL params (but SMP uses them)
 * For this reason dispatching logic is rewritten a bit with the script below.
 * See also tiny scripts within each actions.
 */ 

def requestPath = mockRequest.getPath()
def serviceMetadataMatcher = requestPath =~ /\/(?<participantIdentifier>[^\/]+)\/services\/(?<documentTypeId>[^\/]+)/
def serviceGroupMatcher    = requestPath =~ /\/(?<participantIdentifier>[^\/]+)/
def action = "BAD_REQUEST"
def httpMethod = mockRequest.getMethod().name()
def methodIsSupported = ["GET", "PUT", "DELETE"].contains(httpMethod)

/*
 * {participantIdentifier} = {participantScheme}::{participantId}
 */
def feedContextWithParticipantIds = { matcher ->
	context.participantIdentifier = matcher.group( 'participantIdentifier' )
	def participantIdentifierMatcher = context.participantIdentifier =~/(?<participantScheme>[^\/]+)::(?<participantId>[^\/]+)/
	if( participantIdentifierMatcher.matches()){
		context.participantScheme = participantIdentifierMatcher.group('participantScheme')
		context.participantId     = participantIdentifierMatcher.group('participantId')
	}else{
		context.participantScheme = ""
		context.participantId     = ""
	}
}

/*
 * Common messages dispatcher to be used by all 6 Use Cases
 * Chooses the response with the same name as participantId.
 * Fallbacks to whole participantIdentifier if no double :: was present
 */
context.dispatchMessage = { 
	def messageName = context.participantId ? context.participantId : context.participantIdentifier
	log.info("Dispatched to message: ["+messageName+"] with context: " + context);
	messageName
}

// ===========================
// ======= START HERE ========
// ===========================

log.info "Recieved the request: " + httpMethod + " " + requestPath + " - This HTTP method is supported: " + methodIsSupported

if(methodIsSupported){
	 if(serviceGroupMatcher.matches()){
		feedContextWithParticipantIds(serviceGroupMatcher)
		action = httpMethod + " ServiceGroup"
		log.info action + ", participantIdentifier=[" + context.participantIdentifier + "]"
	} else if(serviceMetadataMatcher.matches()){
		feedContextWithParticipantIds(serviceMetadataMatcher)
		context.documentTypeId = serviceMetadataMatcher.group( 'documentTypeId' )
		action = httpMethod + " ServiceMetadata"
		log.info action + ", participantIdentifier=[" + context.participantIdentifier + "], documentTypeId=[" + context.documentTypeId + "]"
	} else {
		log.info "No groovy match"
	}
}

log.info "Returning response: " + action
mockRunner.mockContext.mockService.getMockOperationByName(action).dispatchRequest(mockRequest)

